mapper.insert:
INSERT INTO EZC_MAPPING_GRP_FACL(	/* 맵핑 그룹 시설 */
	 GRP_FACL_CD	/* 그룹 시설 코드, NUMBER(0,22) */
	,CITY_CD	/* 도시 코드, VARCHAR2(10) */
	,AREA_CD	/* 지역 코드, VARCHAR2(10) */
	,FACL_NM_KOR	/* 시설 명 한글, VARCHAR2(100) */
	,FACL_NM_ENG	/* 시설 명 영문, VARCHAR2(100) */
	,ROOM_CLASS	/* 숙소 등급, VARCHAR2(8) */
	,ROOM_TYPE	/* 숙소 유형, VARCHAR2(8) */
	,ADDR_TYPE	/* 주소 유형, VARCHAR2(8) */
	,ADDR	/* 주소, VARCHAR2(200) */
	,POST	/* 우편번호, VARCHAR2(7) */
	,TEL_NUM	/* 전화 번호, VARCHAR2(20) */
	,COORD_Y	/* 위도, VARCHAR2(20) */
	,COORD_X	/* 경도, VARCHAR2(20) */
	,TRIP_PROP_ID	/* 트립어드바이저 프로퍼티 ID, VARCHAR2(100) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{grpFaclCd, jdbcType=DECIMAL}
	,#{cityCd, jdbcType=VARCHAR}
	,#{areaCd, jdbcType=VARCHAR}
	,#{faclNmKor, jdbcType=VARCHAR}
	,#{faclNmEng, jdbcType=VARCHAR}
	,#{roomClass, jdbcType=VARCHAR}
	,#{roomType, jdbcType=VARCHAR}
	,#{addrType, jdbcType=VARCHAR}
	,#{addr, jdbcType=VARCHAR}
	,#{post, jdbcType=VARCHAR}
	,#{telNum, jdbcType=VARCHAR}
	,#{coordY, jdbcType=VARCHAR}
	,#{coordX, jdbcType=VARCHAR}
	,#{tripPropId, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_MAPPING_GRP_FACL /* 맵핑 그룹 시설 */
	USING DUAL ON (
		GRP_FACL_CD = #{grpFaclCd, jdbcType=DECIMAL}	/* 그룹 시설 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 CITY_CD = #{cityCd, jdbcType=VARCHAR}	/* 도시 코드, VARCHAR2(10) */
			,AREA_CD = #{areaCd, jdbcType=VARCHAR}	/* 지역 코드, VARCHAR2(10) */
			,FACL_NM_KOR = #{faclNmKor, jdbcType=VARCHAR}	/* 시설 명 한글, VARCHAR2(100) */
			,FACL_NM_ENG = #{faclNmEng, jdbcType=VARCHAR}	/* 시설 명 영문, VARCHAR2(100) */
			,ROOM_CLASS = #{roomClass, jdbcType=VARCHAR}	/* 숙소 등급, VARCHAR2(8) */
			,ROOM_TYPE = #{roomType, jdbcType=VARCHAR}	/* 숙소 유형, VARCHAR2(8) */
			,ADDR_TYPE = #{addrType, jdbcType=VARCHAR}	/* 주소 유형, VARCHAR2(8) */
			,ADDR = #{addr, jdbcType=VARCHAR}	/* 주소, VARCHAR2(200) */
			,POST = #{post, jdbcType=VARCHAR}	/* 우편번호, VARCHAR2(7) */
			,TEL_NUM = #{telNum, jdbcType=VARCHAR}	/* 전화 번호, VARCHAR2(20) */
			,COORD_Y = #{coordY, jdbcType=VARCHAR}	/* 위도, VARCHAR2(20) */
			,COORD_X = #{coordX, jdbcType=VARCHAR}	/* 경도, VARCHAR2(20) */
			,TRIP_PROP_ID = #{tripPropId, jdbcType=VARCHAR}	/* 트립어드바이저 프로퍼티 ID, VARCHAR2(100) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 GRP_FACL_CD	/* 그룹 시설 코드, NUMBER(0,22) */
			,CITY_CD	/* 도시 코드, VARCHAR2(10) */
			,AREA_CD	/* 지역 코드, VARCHAR2(10) */
			,FACL_NM_KOR	/* 시설 명 한글, VARCHAR2(100) */
			,FACL_NM_ENG	/* 시설 명 영문, VARCHAR2(100) */
			,ROOM_CLASS	/* 숙소 등급, VARCHAR2(8) */
			,ROOM_TYPE	/* 숙소 유형, VARCHAR2(8) */
			,ADDR_TYPE	/* 주소 유형, VARCHAR2(8) */
			,ADDR	/* 주소, VARCHAR2(200) */
			,POST	/* 우편번호, VARCHAR2(7) */
			,TEL_NUM	/* 전화 번호, VARCHAR2(20) */
			,COORD_Y	/* 위도, VARCHAR2(20) */
			,COORD_X	/* 경도, VARCHAR2(20) */
			,TRIP_PROP_ID	/* 트립어드바이저 프로퍼티 ID, VARCHAR2(100) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{grpFaclCd, jdbcType=DECIMAL}
			,#{cityCd, jdbcType=VARCHAR}
			,#{areaCd, jdbcType=VARCHAR}
			,#{faclNmKor, jdbcType=VARCHAR}
			,#{faclNmEng, jdbcType=VARCHAR}
			,#{roomClass, jdbcType=VARCHAR}
			,#{roomType, jdbcType=VARCHAR}
			,#{addrType, jdbcType=VARCHAR}
			,#{addr, jdbcType=VARCHAR}
			,#{post, jdbcType=VARCHAR}
			,#{telNum, jdbcType=VARCHAR}
			,#{coordY, jdbcType=VARCHAR}
			,#{coordX, jdbcType=VARCHAR}
			,#{tripPropId, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
