mapper.insert:
INSERT INTO EZC_MEM(	/* 회원 정보 */
	 USER_ID	/* 회원 ID, VARCHAR2(20) */
	,CLIENT_CD	/* 고객사 코드, VARCHAR2(20) */
	,USER_KEY	/* USER_KEY, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{userId, jdbcType=VARCHAR}
	,#{clientCd, jdbcType=VARCHAR}
	,#{userKey, jdbcType=DECIMAL}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_MEM /* 회원 정보 */
	USING DUAL ON (
		USER_ID = #{userId, jdbcType=VARCHAR}	/* 회원 ID, VARCHAR2(20) */
		AND CLIENT_CD = #{clientCd, jdbcType=VARCHAR}	/* 고객사 코드, VARCHAR2(20) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 USER_KEY = #{userKey, jdbcType=DECIMAL}	/* USER_KEY, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 USER_ID	/* 회원 ID, VARCHAR2(20) */
			,CLIENT_CD	/* 고객사 코드, VARCHAR2(20) */
			,USER_KEY	/* USER_KEY, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{userId, jdbcType=VARCHAR}
			,#{clientCd, jdbcType=VARCHAR}
			,#{userKey, jdbcType=DECIMAL}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
