mapper.insert:
INSERT INTO EZC_MENU(	/* 메뉴 정보 */
	 MENU_ID	/* 메뉴 ID, VARCHAR2(6) */
	,HIGH_MENU_ID	/* 상위 메뉴 ID, VARCHAR2(6) */
	,MENU_LEVEL	/* 메뉴 레벨, VARCHAR2(18) */
	,MENU_NM	/* 메뉴 명, VARCHAR2(50) */
	,MENU_URL	/* 메뉴 URL, VARCHAR2(200) */
	,DISP_ORDER	/* 정렬순서, NUMBER(0,22) */
	,USE_YN	/* 사용 여부, CHAR(1) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{menuId, jdbcType=VARCHAR}
	,#{highMenuId, jdbcType=VARCHAR}
	,#{menuLevel, jdbcType=VARCHAR}
	,#{menuNm, jdbcType=VARCHAR}
	,#{menuUrl, jdbcType=VARCHAR}
	,#{dispOrder, jdbcType=NUMERIC}
	,#{useYn, jdbcType=CHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_MENU /* 메뉴 정보 */
	USING DUAL ON (
		MENU_ID = #{menuId, jdbcType=VARCHAR}	/* 메뉴 ID, VARCHAR2(6) */
		AND HIGH_MENU_ID = #{highMenuId, jdbcType=VARCHAR}	/* 상위 메뉴 ID, VARCHAR2(6) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 MENU_LEVEL = #{menuLevel, jdbcType=VARCHAR}	/* 메뉴 레벨, VARCHAR2(18) */
			,MENU_NM = #{menuNm, jdbcType=VARCHAR}	/* 메뉴 명, VARCHAR2(50) */
			,MENU_URL = #{menuUrl, jdbcType=VARCHAR}	/* 메뉴 URL, VARCHAR2(200) */
			,DISP_ORDER = #{dispOrder, jdbcType=NUMERIC}	/* 정렬순서, NUMBER(0,22) */
			,USE_YN = #{useYn, jdbcType=CHAR}	/* 사용 여부, CHAR(1) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 MENU_ID	/* 메뉴 ID, VARCHAR2(6) */
			,HIGH_MENU_ID	/* 상위 메뉴 ID, VARCHAR2(6) */
			,MENU_LEVEL	/* 메뉴 레벨, VARCHAR2(18) */
			,MENU_NM	/* 메뉴 명, VARCHAR2(50) */
			,MENU_URL	/* 메뉴 URL, VARCHAR2(200) */
			,DISP_ORDER	/* 정렬순서, NUMBER(0,22) */
			,USE_YN	/* 사용 여부, CHAR(1) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{menuId, jdbcType=VARCHAR}
			,#{highMenuId, jdbcType=VARCHAR}
			,#{menuLevel, jdbcType=VARCHAR}
			,#{menuNm, jdbcType=VARCHAR}
			,#{menuUrl, jdbcType=VARCHAR}
			,#{dispOrder, jdbcType=NUMERIC}
			,#{useYn, jdbcType=CHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
