mapper.insert:
INSERT INTO EZC_MGR_LOG(	/* 운영자 로그 */
	 LOG_SEQ	/* 로그 일련번호, NUMBER(0,22) */
	,MGR_ID	/* 운영자 ID, VARCHAR2(20) */
	,LOG_TYPE	/* 로그 유형, VARCHAR2(8) */
	,CONTENT	/* 내용, VARCHAR2(1000) */
	,CONN_IP	/* 접속 IP, VARCHAR2(15) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{logSeq, jdbcType=DECIMAL}
	,#{mgrId, jdbcType=VARCHAR}
	,#{logType, jdbcType=VARCHAR}
	,#{content, jdbcType=VARCHAR}
	,#{connIp, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_MGR_LOG /* 운영자 로그 */
	USING DUAL ON (
		LOG_SEQ = #{logSeq, jdbcType=DECIMAL}	/* 로그 일련번호, NUMBER(0,22) */
		AND MGR_ID = #{mgrId, jdbcType=VARCHAR}	/* 운영자 ID, VARCHAR2(20) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 LOG_TYPE = #{logType, jdbcType=VARCHAR}	/* 로그 유형, VARCHAR2(8) */
			,CONTENT = #{content, jdbcType=VARCHAR}	/* 내용, VARCHAR2(1000) */
			,CONN_IP = #{connIp, jdbcType=VARCHAR}	/* 접속 IP, VARCHAR2(15) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 LOG_SEQ	/* 로그 일련번호, NUMBER(0,22) */
			,MGR_ID	/* 운영자 ID, VARCHAR2(20) */
			,LOG_TYPE	/* 로그 유형, VARCHAR2(8) */
			,CONTENT	/* 내용, VARCHAR2(1000) */
			,CONN_IP	/* 접속 IP, VARCHAR2(15) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{logSeq, jdbcType=DECIMAL}
			,#{mgrId, jdbcType=VARCHAR}
			,#{logType, jdbcType=VARCHAR}
			,#{content, jdbcType=VARCHAR}
			,#{connIp, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
