mapper.insert:
INSERT INTO EZC_MGR(	/* 운영자 정보 */
	 MGR_ID	/* 운영자 ID, VARCHAR2(20) */
	,MGR_TYPE	/* 운영자 유형, VARCHAR2(8) */
	,CLIENT_CD	/* 고객사 코드, VARCHAR2(20) */
	,PARTNER_CD	/* 제휴사 코드, VARCHAR2(20) */
	,MGR_NM	/* 운영자 명, VARCHAR2(20) */
	,PASSWD	/* 비밀번호, VARCHAR2(100) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{mgrId, jdbcType=VARCHAR}
	,#{mgrType, jdbcType=VARCHAR}
	,#{clientCd, jdbcType=VARCHAR}
	,#{partnerCd, jdbcType=VARCHAR}
	,#{mgrNm, jdbcType=VARCHAR}
	,#{passwd, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_MGR /* 운영자 정보 */
	USING DUAL ON (
		MGR_ID = #{mgrId, jdbcType=VARCHAR}	/* 운영자 ID, VARCHAR2(20) */
		AND CLIENT_CD = #{clientCd, jdbcType=VARCHAR}	/* 고객사 코드, VARCHAR2(20) */
		AND PARTNER_CD = #{partnerCd, jdbcType=VARCHAR}	/* 제휴사 코드, VARCHAR2(20) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 MGR_TYPE = #{mgrType, jdbcType=VARCHAR}	/* 운영자 유형, VARCHAR2(8) */
			,MGR_NM = #{mgrNm, jdbcType=VARCHAR}	/* 운영자 명, VARCHAR2(20) */
			,PASSWD = #{passwd, jdbcType=VARCHAR}	/* 비밀번호, VARCHAR2(100) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 MGR_ID	/* 운영자 ID, VARCHAR2(20) */
			,MGR_TYPE	/* 운영자 유형, VARCHAR2(8) */
			,CLIENT_CD	/* 고객사 코드, VARCHAR2(20) */
			,PARTNER_CD	/* 제휴사 코드, VARCHAR2(20) */
			,MGR_NM	/* 운영자 명, VARCHAR2(20) */
			,PASSWD	/* 비밀번호, VARCHAR2(100) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{mgrId, jdbcType=VARCHAR}
			,#{mgrType, jdbcType=VARCHAR}
			,#{clientCd, jdbcType=VARCHAR}
			,#{partnerCd, jdbcType=VARCHAR}
			,#{mgrNm, jdbcType=VARCHAR}
			,#{passwd, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
