mapper.insert:
INSERT INTO EZC_MGR_NOTICE_ATTACH(	/* 운영자 공지사항 첨부파일 */
	 NOTICE_ATTACH_SEQ	/* 공지사항 첨부파일 일련번호, NUMBER(0,22) */
	,NOTICE_SEQ	/* 공지사항 일련번호, NUMBER(0,22) */
	,FILE_PATH	/* 파일 경로, VARCHAR2(100) */
	,SYS_FILENM	/* 시스템 파일명, VARCHAR2(100) */
	,FILENM	/* 파일명, VARCHAR2(100) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{noticeAttachSeq, jdbcType=DECIMAL}
	,#{noticeSeq, jdbcType=DECIMAL}
	,#{filePath, jdbcType=VARCHAR}
	,#{sysFilenm, jdbcType=VARCHAR}
	,#{filenm, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_MGR_NOTICE_ATTACH /* 운영자 공지사항 첨부파일 */
	USING DUAL ON (
		NOTICE_ATTACH_SEQ = #{noticeAttachSeq, jdbcType=DECIMAL}	/* 공지사항 첨부파일 일련번호, NUMBER(0,22) */
		AND NOTICE_SEQ = #{noticeSeq, jdbcType=DECIMAL}	/* 공지사항 일련번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 FILE_PATH = #{filePath, jdbcType=VARCHAR}	/* 파일 경로, VARCHAR2(100) */
			,SYS_FILENM = #{sysFilenm, jdbcType=VARCHAR}	/* 시스템 파일명, VARCHAR2(100) */
			,FILENM = #{filenm, jdbcType=VARCHAR}	/* 파일명, VARCHAR2(100) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 NOTICE_ATTACH_SEQ	/* 공지사항 첨부파일 일련번호, NUMBER(0,22) */
			,NOTICE_SEQ	/* 공지사항 일련번호, NUMBER(0,22) */
			,FILE_PATH	/* 파일 경로, VARCHAR2(100) */
			,SYS_FILENM	/* 시스템 파일명, VARCHAR2(100) */
			,FILENM	/* 파일명, VARCHAR2(100) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{noticeAttachSeq, jdbcType=DECIMAL}
			,#{noticeSeq, jdbcType=DECIMAL}
			,#{filePath, jdbcType=VARCHAR}
			,#{sysFilenm, jdbcType=VARCHAR}
			,#{filenm, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
