mapper.insert:
INSERT INTO EZC_PARTNER_EZWEL_MGR(	/* 제휴사 이지웰 담당자 */
	 PARTNER_EZWEL_SEQ	/* 제휴사 이지웰 일련번호, NUMBER(0,22) */
	,PARTNER_CD	/* 제휴사 코드, VARCHAR2(20) */
	,MGR_ID	/* 운영자 ID, VARCHAR2(20) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{partnerEzwelSeq, jdbcType=DECIMAL}
	,#{partnerCd, jdbcType=VARCHAR}
	,#{mgrId, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_PARTNER_EZWEL_MGR /* 제휴사 이지웰 담당자 */
	USING DUAL ON (
		PARTNER_EZWEL_SEQ = #{partnerEzwelSeq, jdbcType=DECIMAL}	/* 제휴사 이지웰 일련번호, NUMBER(0,22) */
		AND PARTNER_CD = #{partnerCd, jdbcType=VARCHAR}	/* 제휴사 코드, VARCHAR2(20) */
		AND MGR_ID = #{mgrId, jdbcType=VARCHAR}	/* 운영자 ID, VARCHAR2(20) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 PARTNER_EZWEL_SEQ	/* 제휴사 이지웰 일련번호, NUMBER(0,22) */
			,PARTNER_CD	/* 제휴사 코드, VARCHAR2(20) */
			,MGR_ID	/* 운영자 ID, VARCHAR2(20) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{partnerEzwelSeq, jdbcType=DECIMAL}
			,#{partnerCd, jdbcType=VARCHAR}
			,#{mgrId, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
