mapper.select:
SELECT * FROM ( SELECT ROWNUM AS ROW__NUM, RECORDS.* FROM ( /* #### SQL Body [[ ################# */ SELECT EZC_PARTNER.PARTNER_CD AS partnerCd /* 제휴사 코드, VARCHAR2(20) */ ,EZC_PARTNER.PARTNER_TYPE AS partnerType /* 제휴사 유형, VARCHAR2(8) */ ,EZC_PARTNER.PARTNER_CD_TYPE AS partnerCdType /* 제휴사 코드 유형, VARCHAR2(8) */ ,EZC_PARTNER.PAY_DIV AS payDiv /* 지불 구분, VARCHAR2(8) */ ,EZC_PARTNER.CAL_STD_TYPE AS calStdType /* 정산 기준 유형, VARCHAR2(8) */ ,EZC_PARTNER.AMT_STD_TYPE AS amtStdType /* 금액 기준 유형, VARCHAR2(8) */ ,EZC_PARTNER.PARTNER_NM AS partnerNm /* 제휴사 명, VARCHAR2(100) */ ,EZC_PARTNER.LIC_REGNUM AS licRegnum /* 사업자 등록번호, VARCHAR2(12) */ ,EZC_PARTNER.CEONM AS ceonm /* 대표자명, VARCHAR2(20) */ ,EZC_PARTNER.TEL_NUM AS telNum /* 전화 번호, VARCHAR2(20) */ ,EZC_PARTNER.FAX_NUM AS faxNum /* 팩스 번호, VARCHAR2(100) */ ,EZC_PARTNER.ADDR AS addr /* 주소, VARCHAR2(100) */ ,EZC_PARTNER.DETAIL_ADDR AS detailAddr /* 상세 주소, VARCHAR2(100) */ ,EZC_PARTNER.EZWEL_MGR_ID AS ezwelMgrId /* 이지웰 담당자 ID, VARCHAR2(20) */ ,EZC_PARTNER.REG_ID AS regId /* 등록자 ID, VARCHAR2(20) */ ,EZC_PARTNER.REG_DT AS regDt /* 등록 일시, VARCHAR2(14) */ ,EZC_PARTNER.MODI_ID AS modiId /* 수정자 ID, VARCHAR2(20) */ ,EZC_PARTNER.MODI_DT AS modiDt /* 수정 일시, VARCHAR2(14) */ FROM EZC_PARTNER /* 제휴사 정보 */ WHERE 1=1
AND EZC_PARTNER.PARTNER_CD = #{param.partnerCd, jdbcType=VARCHAR} /* 제휴사 코드, VARCHAR2(20) */
ORDER BY EZC_PARTNER.PARTNER_CD DESC /* #### SQL Body ]] ################# */ ) RECORDS WHERE ROWNUM <= ((#{param.pageNum, jdbcType=NUMERIC}*#{param.pageCount, jdbcType=NUMERIC})+1) ) WHERE ROW__NUM > (#{param.pageNum, jdbcType=NUMERIC}-1)*#{param.pageCount, jdbcType=NUMERIC}

mapper.update:
MERGE INTO EZC_PARTNER /* 제휴사 정보 */
	USING DUAL ON (
		PARTNER_CD = #{partnerCd, jdbcType=VARCHAR}	/* 제휴사 코드, VARCHAR2(20) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 PARTNER_TYPE = #{partnerType, jdbcType=VARCHAR}	/* 제휴사 유형, VARCHAR2(8) */
			,PARTNER_CD_TYPE = #{partnerCdType, jdbcType=VARCHAR}	/* 제휴사 코드 유형, VARCHAR2(8) */
			,PAY_DIV = #{payDiv, jdbcType=VARCHAR}	/* 지불 구분, VARCHAR2(8) */
			,CAL_STD_TYPE = #{calStdType, jdbcType=VARCHAR}	/* 정산 기준 유형, VARCHAR2(8) */
			,AMT_STD_TYPE = #{amtStdType, jdbcType=VARCHAR}	/* 금액 기준 유형, VARCHAR2(8) */
			,PARTNER_NM = #{partnerNm, jdbcType=VARCHAR}	/* 제휴사 명, VARCHAR2(100) */
			,LIC_REGNUM = #{licRegnum, jdbcType=VARCHAR}	/* 사업자 등록번호, VARCHAR2(12) */
			,CEONM = #{ceonm, jdbcType=VARCHAR}	/* 대표자명, VARCHAR2(20) */
			,TEL_NUM = #{telNum, jdbcType=VARCHAR}	/* 전화 번호, VARCHAR2(20) */
			,FAX_NUM = #{faxNum, jdbcType=VARCHAR}	/* 팩스 번호, VARCHAR2(100) */
			,ADDR = #{addr, jdbcType=VARCHAR}	/* 주소, VARCHAR2(100) */
			,DETAIL_ADDR = #{detailAddr, jdbcType=VARCHAR}	/* 상세 주소, VARCHAR2(100) */
			,EZWEL_MGR_ID = #{ezwelMgrId, jdbcType=VARCHAR}	/* 이지웰 담당자 ID, VARCHAR2(20) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 PARTNER_CD	/* 제휴사 코드, VARCHAR2(20) */
			,PARTNER_TYPE	/* 제휴사 유형, VARCHAR2(8) */
