			,AMT_STD_TYPE	/* 금액 기준 유형, VARCHAR2(8) */
			,PARTNER_NM	/* 제휴사 명, VARCHAR2(100) */
			,LIC_REGNUM	/* 사업자 등록번호, VARCHAR2(12) */
			,CEONM	/* 대표자명, VARCHAR2(20) */
			,TEL_NUM	/* 전화 번호, VARCHAR2(20) */
			,FAX_NUM	/* 팩스 번호, VARCHAR2(100) */
			,ADDR	/* 주소, VARCHAR2(100) */
			,DETAIL_ADDR	/* 상세 주소, VARCHAR2(100) */
			,EZWEL_MGR_ID	/* 이지웰 담당자 ID, VARCHAR2(20) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
