mapper.insert:
INSERT INTO EZC_POPUL_SEARCH(	/* 인기 검색어 */
	 SEARCH_SEQ	/* 검색어 일련번호, NUMBER(0,22) */
	,SEARCH	/* 검색어, VARCHAR2(100) */
	,SEARCH_DD	/* 검색 일자, VARCHAR2(8) */
	,REAL_VIEW_CNT	/* 실제 조회수, NUMBER(0,22) */
	,MNG_VIEW_CNT	/* 관리 조회수, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{searchSeq, jdbcType=DECIMAL}
	,#{search, jdbcType=VARCHAR}
	,#{searchDd, jdbcType=VARCHAR}
	,#{realViewCnt, jdbcType=NUMERIC}
	,#{mngViewCnt, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_POPUL_SEARCH /* 인기 검색어 */
	USING DUAL ON (
		SEARCH_SEQ = #{searchSeq, jdbcType=DECIMAL}	/* 검색어 일련번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 SEARCH = #{search, jdbcType=VARCHAR}	/* 검색어, VARCHAR2(100) */
			,SEARCH_DD = #{searchDd, jdbcType=VARCHAR}	/* 검색 일자, VARCHAR2(8) */
			,REAL_VIEW_CNT = #{realViewCnt, jdbcType=NUMERIC}	/* 실제 조회수, NUMBER(0,22) */
			,MNG_VIEW_CNT = #{mngViewCnt, jdbcType=NUMERIC}	/* 관리 조회수, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 SEARCH_SEQ	/* 검색어 일련번호, NUMBER(0,22) */
			,SEARCH	/* 검색어, VARCHAR2(100) */
			,SEARCH_DD	/* 검색 일자, VARCHAR2(8) */
			,REAL_VIEW_CNT	/* 실제 조회수, NUMBER(0,22) */
			,MNG_VIEW_CNT	/* 관리 조회수, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{searchSeq, jdbcType=DECIMAL}
			,#{search, jdbcType=VARCHAR}
			,#{searchDd, jdbcType=VARCHAR}
			,#{realViewCnt, jdbcType=NUMERIC}
			,#{mngViewCnt, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
