mapper.insert:
INSERT INTO EZC_POPUL_SEARCH(	/* 인기 검색어 */
	 SEARCH_SEQ	/* 검색어 일련번호, NUMBER(0,22) */
	,SEARCH	/* 검색어, VARCHAR2(100) */
	,SEARCH_DD	/* 검색 일자, VARCHAR2(8) */
	,REAL_VIEW_CNT	/* 실제 조회수, NUMBER(0,22) */
	,MNG_VIEW_CNT	/* 관리 조회수, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{searchSeq, jdbcType=DECIMAL}
	,#{search, jdbcType=VARCHAR}
	,#{searchDd, jdbcType=VARCHAR}
	,#{realViewCnt, jdbcType=NUMERIC}
	,#{mngViewCnt, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS ROW__NUM, RECORDS.* FROM ( /* #### SQL Body [[ ################# */ SELECT EZC_POPUL_SEARCH.SEARCH_SEQ AS searchSeq /* 검색어 일련번호, NUMBER(0,22) */ ,EZC_POPUL_SEARCH.SEARCH AS search /* 검색어, VARCHAR2(100) */ ,EZC_POPUL_SEARCH.SEARCH_DD AS searchDd /* 검색 일자, VARCHAR2(8) */ ,EZC_POPUL_SEARCH.REAL_VIEW_CNT AS realViewCnt /* 실제 조회수, NUMBER(0,22) */ ,EZC_POPUL_SEARCH.MNG_VIEW_CNT AS mngViewCnt /* 관리 조회수, NUMBER(0,22) */ ,EZC_POPUL_SEARCH.REG_ID AS regId /* 등록자 ID, VARCHAR2(20) */ ,EZC_POPUL_SEARCH.REG_DT AS regDt /* 등록 일시, VARCHAR2(14) */ ,EZC_POPUL_SEARCH.MODI_ID AS modiId /* 수정자 ID, VARCHAR2(20) */ ,EZC_POPUL_SEARCH.MODI_DT AS modiDt /* 수정 일시, VARCHAR2(14) */ FROM EZC_POPUL_SEARCH /* 인기 검색어 */ WHERE 1=1
AND EZC_POPUL_SEARCH.SEARCH_SEQ = #{param.searchSeq, jdbcType=DECIMAL} /* 검색어 일련번호, NUMBER(0,22) */
ORDER BY EZC_POPUL_SEARCH.SEARCH_SEQ DESC /* #### SQL Body ]] ################# */ ) RECORDS WHERE ROWNUM <= ((#{param.pageNum, jdbcType=NUMERIC}*#{param.pageCount, jdbcType=NUMERIC})+1) ) WHERE ROW__NUM > (#{param.pageNum, jdbcType=NUMERIC}-1)*#{param.pageCount, jdbcType=NUMERIC}

mapper.update:
MERGE INTO EZC_POPUL_SEARCH /* 인기 검색어 */
	USING DUAL ON (
		SEARCH_SEQ = #{searchSeq, jdbcType=DECIMAL}	/* 검색어 일련번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 SEARCH = #{search, jdbcType=VARCHAR}	/* 검색어, VARCHAR2(100) */
			,SEARCH_DD = #{searchDd, jdbcType=VARCHAR}	/* 검색 일자, VARCHAR2(8) */
			,REAL_VIEW_CNT = #{realViewCnt, jdbcType=NUMERIC}	/* 실제 조회수, NUMBER(0,22) */
			,MNG_VIEW_CNT = #{mngViewCnt, jdbcType=NUMERIC}	/* 관리 조회수, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 SEARCH_SEQ	/* 검색어 일련번호, NUMBER(0,22) */
			,SEARCH	/* 검색어, VARCHAR2(100) */
			,SEARCH_DD	/* 검색 일자, VARCHAR2(8) */
			,REAL_VIEW_CNT	/* 실제 조회수, NUMBER(0,22) */
			,MNG_VIEW_CNT	/* 관리 조회수, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{searchSeq, jdbcType=DECIMAL}
			,#{search, jdbcType=VARCHAR}
			,#{searchDd, jdbcType=VARCHAR}
			,#{realViewCnt, jdbcType=NUMERIC}
			,#{mngViewCnt, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
