mapper.insert:
INSERT INTO EZC_PRM_CLIENT(	/* 프로모션 고객사 */
	 PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
	,CHANNEL_CD	/* 채널 코드, VARCHAR2(10) */
	,CLIENT_CD	/* 고객사 코드, VARCHAR2(20) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{prmCd, jdbcType=VARCHAR}
	,#{channelCd, jdbcType=VARCHAR}
	,#{clientCd, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_PRM_CLIENT /* 프로모션 고객사 */
	USING DUAL ON (
		PRM_CD = #{prmCd, jdbcType=VARCHAR}	/* 프로모션 코드, VARCHAR2(10) */
		AND CHANNEL_CD = #{channelCd, jdbcType=VARCHAR}	/* 채널 코드, VARCHAR2(10) */
		AND CLIENT_CD = #{clientCd, jdbcType=VARCHAR}	/* 고객사 코드, VARCHAR2(20) */
	)
	WHEN MATCHED THEN
		UPDATE SET
	WHEN NOT MATCHED THEN
		INSERT (
			 PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
			,CHANNEL_CD	/* 채널 코드, VARCHAR2(10) */
			,CLIENT_CD	/* 고객사 코드, VARCHAR2(20) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{prmCd, jdbcType=VARCHAR}
			,#{channelCd, jdbcType=VARCHAR}
			,#{clientCd, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
