mapper.insert:
INSERT INTO EZC_PRM_CPN(	/* 프로모션 쿠폰 */
	 PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
	,CPN_CD	/* 쿠폰 코드, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{prmCd, jdbcType=VARCHAR}
	,#{cpnCd, jdbcType=DECIMAL}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_PRM_CPN /* 프로모션 쿠폰 */
	USING DUAL ON (
		PRM_CD = #{prmCd, jdbcType=VARCHAR}	/* 프로모션 코드, VARCHAR2(10) */
		AND CPN_CD = #{cpnCd, jdbcType=DECIMAL}	/* 쿠폰 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
	WHEN NOT MATCHED THEN
		INSERT (
			 PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
			,CPN_CD	/* 쿠폰 코드, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{prmCd, jdbcType=VARCHAR}
			,#{cpnCd, jdbcType=DECIMAL}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
