mapper.insert:
INSERT INTO EZC_PRM_MNG(	/* 프로모션 관리 */
	 PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
	,PRM_TYPE	/* 프로모션 유형, VARCHAR2(8) */
	,PRM_CONFIG_DIV	/* 프로모션 구성 구분, VARCHAR2(8) */
	,TITLE	/* 제목, VARCHAR2(100) */
	,PRM_START_DD	/* 프로모션 시작 일자, VARCHAR2(8) */
	,PRM_END_DD	/* 프로모션 종료 일자, VARCHAR2(8) */
	,PRM_START_TM	/* 프로모션 시작 시간, VARCHAR2(4) */
	,PRM_END_TM	/* 프로모션 종료 시간, VARCHAR2(4) */
	,PRM_USE_YN	/* 프로모션 사용 여부, CHAR(1) */
	,DEVICE_TYPE	/* 디바이스 유형, VARCHAR2(8) */
	,FAMUSER_ACCESS_YN	/* 가족회원 접근 여부, CHAR(1) */
	,LIST_IMG	/* 목록 이미지, VARCHAR2(200) */
	,DETAIL_HTML	/* 상세 HTML, VARCHAR2(4000) */
	,NOTICE	/* 유의사항, VARCHAR2(2000) */
	,GOOD_USE_YN	/* 좋아요 사용 여부, CHAR(1) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{prmCd, jdbcType=VARCHAR}
	,#{prmType, jdbcType=VARCHAR}
	,#{prmConfigDiv, jdbcType=VARCHAR}
	,#{title, jdbcType=VARCHAR}
	,#{prmStartDd, jdbcType=VARCHAR}
	,#{prmEndDd, jdbcType=VARCHAR}
	,#{prmStartTm, jdbcType=VARCHAR}
	,#{prmEndTm, jdbcType=VARCHAR}
	,#{prmUseYn, jdbcType=CHAR}
	,#{deviceType, jdbcType=VARCHAR}
	,#{famuserAccessYn, jdbcType=CHAR}
	,#{listImg, jdbcType=VARCHAR}
	,#{detailHtml, jdbcType=VARCHAR}
	,#{notice, jdbcType=VARCHAR}
	,#{goodUseYn, jdbcType=CHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_PRM_MNG /* 프로모션 관리 */
	USING DUAL ON (
		PRM_CD = #{prmCd, jdbcType=VARCHAR}	/* 프로모션 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 PRM_TYPE = #{prmType, jdbcType=VARCHAR}	/* 프로모션 유형, VARCHAR2(8) */
			,PRM_CONFIG_DIV = #{prmConfigDiv, jdbcType=VARCHAR}	/* 프로모션 구성 구분, VARCHAR2(8) */
			,TITLE = #{title, jdbcType=VARCHAR}	/* 제목, VARCHAR2(100) */
			,PRM_START_DD = #{prmStartDd, jdbcType=VARCHAR}	/* 프로모션 시작 일자, VARCHAR2(8) */
			,PRM_END_DD = #{prmEndDd, jdbcType=VARCHAR}	/* 프로모션 종료 일자, VARCHAR2(8) */
			,PRM_START_TM = #{prmStartTm, jdbcType=VARCHAR}	/* 프로모션 시작 시간, VARCHAR2(4) */
			,PRM_END_TM = #{prmEndTm, jdbcType=VARCHAR}	/* 프로모션 종료 시간, VARCHAR2(4) */
			,PRM_USE_YN = #{prmUseYn, jdbcType=CHAR}	/* 프로모션 사용 여부, CHAR(1) */
			,DEVICE_TYPE = #{deviceType, jdbcType=VARCHAR}	/* 디바이스 유형, VARCHAR2(8) */
			,FAMUSER_ACCESS_YN = #{famuserAccessYn, jdbcType=CHAR}	/* 가족회원 접근 여부, CHAR(1) */
			,LIST_IMG = #{listImg, jdbcType=VARCHAR}	/* 목록 이미지, VARCHAR2(200) */
			,DETAIL_HTML = #{detailHtml, jdbcType=VARCHAR}	/* 상세 HTML, VARCHAR2(4000) */
			,NOTICE = #{notice, jdbcType=VARCHAR}	/* 유의사항, VARCHAR2(2000) */
			,GOOD_USE_YN = #{goodUseYn, jdbcType=CHAR}	/* 좋아요 사용 여부, CHAR(1) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
			,PRM_TYPE	/* 프로모션 유형, VARCHAR2(8) */
			,PRM_CONFIG_DIV	/* 프로모션 구성 구분, VARCHAR2(8) */
			,TITLE	/* 제목, VARCHAR2(100) */
			,PRM_START_DD	/* 프로모션 시작 일자, VARCHAR2(8) */
			,PRM_END_DD	/* 프로모션 종료 일자, VARCHAR2(8) */
			,PRM_START_TM	/* 프로모션 시작 시간, VARCHAR2(4) */
			,PRM_END_TM	/* 프로모션 종료 시간, VARCHAR2(4) */
			,PRM_USE_YN	/* 프로모션 사용 여부, CHAR(1) */
			,DEVICE_TYPE	/* 디바이스 유형, VARCHAR2(8) */
			,FAMUSER_ACCESS_YN	/* 가족회원 접근 여부, CHAR(1) */
			,LIST_IMG	/* 목록 이미지, VARCHAR2(200) */
			,DETAIL_HTML	/* 상세 HTML, VARCHAR2(4000) */
			,NOTICE	/* 유의사항, VARCHAR2(2000) */
			,GOOD_USE_YN	/* 좋아요 사용 여부, CHAR(1) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{prmCd, jdbcType=VARCHAR}
			,#{prmType, jdbcType=VARCHAR}
			,#{prmConfigDiv, jdbcType=VARCHAR}
			,#{title, jdbcType=VARCHAR}
			,#{prmStartDd, jdbcType=VARCHAR}
			,#{prmEndDd, jdbcType=VARCHAR}
