mapper.insert:
INSERT INTO EZC_PRM_SURVEY_ANSWER(	/* 프로모션 설문 답변 */
	 PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
	,ANSWER_NUM	/* 답변 번호, NUMBER(0,22) */
	,ANSWER_CONTENT	/* 답변 내용, VARCHAR2(1000) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{prmCd, jdbcType=VARCHAR}
	,#{answerNum, jdbcType=NUMERIC}
	,#{answerContent, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_PRM_SURVEY_ANSWER /* 프로모션 설문 답변 */
	USING DUAL ON (
		PRM_CD = #{prmCd, jdbcType=VARCHAR}	/* 프로모션 코드, VARCHAR2(10) */
		AND ANSWER_NUM = #{answerNum, jdbcType=NUMERIC}	/* 답변 번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 ANSWER_CONTENT = #{answerContent, jdbcType=VARCHAR}	/* 답변 내용, VARCHAR2(1000) */
	WHEN NOT MATCHED THEN
		INSERT (
			 PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
			,ANSWER_NUM	/* 답변 번호, NUMBER(0,22) */
			,ANSWER_CONTENT	/* 답변 내용, VARCHAR2(1000) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{prmCd, jdbcType=VARCHAR}
			,#{answerNum, jdbcType=NUMERIC}
			,#{answerContent, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
