mapper.insert:
INSERT INTO EZC_PRM_SURVEY(	/* 프로모션 설문 */
	 PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
	,SURVEY_TITLE	/* 설문 제목, VARCHAR2(100) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{prmCd, jdbcType=VARCHAR}
	,#{surveyTitle, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS ROW__NUM, RECORDS.* FROM ( /* #### SQL Body [[ ################# */ SELECT EZC_PRM_SURVEY.PRM_CD AS prmCd /* 프로모션 코드, VARCHAR2(10) */ ,EZC_PRM_SURVEY.SURVEY_TITLE AS surveyTitle /* 설문 제목, VARCHAR2(100) */ ,EZC_PRM_SURVEY.REG_ID AS regId /* 등록자 ID, VARCHAR2(20) */ ,EZC_PRM_SURVEY.REG_DT AS regDt /* 등록 일시, VARCHAR2(14) */ FROM EZC_PRM_SURVEY /* 프로모션 설문 */ ,EZC_PRM_MNG /* 프로모션 관리 */ WHERE 1=1 AND EZC_PRM_SURVEY.PRM_CD = EZC_PRM_MNG.PRM_CD AND EZC_PRM_SURVEY.REC_STAT = #{param.recStat, jdbcType=CHAR} /* 레코드 상태, CHAR(1) */ AND EZC_PRM_MNG.REC_STAT = #{param.recStat, jdbcType=CHAR} /* 레코드 상태, CHAR(1) */
AND EZC_PRM_SURVEY.PRM_CD = #{param.prmCd, jdbcType=VARCHAR} /* 프로모션 코드, VARCHAR2(10) */
ORDER BY EZC_PRM_SURVEY.PRM_CD DESC /* #### SQL Body ]] ################# */ ) RECORDS WHERE ROWNUM <= ((#{param.pageNum, jdbcType=NUMERIC}*#{param.pageCount, jdbcType=NUMERIC})+1) ) WHERE ROW__NUM > (#{param.pageNum, jdbcType=NUMERIC}-1)*#{param.pageCount, jdbcType=NUMERIC}

mapper.update:
MERGE INTO EZC_PRM_SURVEY /* 프로모션 설문 */
	USING DUAL ON (
		PRM_CD = #{prmCd, jdbcType=VARCHAR}	/* 프로모션 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 SURVEY_TITLE = #{surveyTitle, jdbcType=VARCHAR}	/* 설문 제목, VARCHAR2(100) */
	WHEN NOT MATCHED THEN
		INSERT (
			 PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
			,SURVEY_TITLE	/* 설문 제목, VARCHAR2(100) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{prmCd, jdbcType=VARCHAR}
			,#{surveyTitle, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
