mapper.insert:
INSERT INTO EZC_PRM_SURVEY(	/* 프로모션 설문 */
	 PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
	,SURVEY_TITLE	/* 설문 제목, VARCHAR2(100) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{prmCd, jdbcType=VARCHAR}
	,#{surveyTitle, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_PRM_SURVEY /* 프로모션 설문 */
	USING DUAL ON (
		PRM_CD = #{prmCd, jdbcType=VARCHAR}	/* 프로모션 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 SURVEY_TITLE = #{surveyTitle, jdbcType=VARCHAR}	/* 설문 제목, VARCHAR2(100) */
	WHEN NOT MATCHED THEN
		INSERT (
			 PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
			,SURVEY_TITLE	/* 설문 제목, VARCHAR2(100) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{prmCd, jdbcType=VARCHAR}
			,#{surveyTitle, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
