mapper.insert:
INSERT INTO EZC_PRM_SURVEY_USER(	/* 프로모션 설문 참여자 */
	 PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
	,USER_ID	/* 회원 ID, VARCHAR2(20) */
	,ANSWER_NUM	/* 답변 번호, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{prmCd, jdbcType=VARCHAR}
	,#{userId, jdbcType=VARCHAR}
	,#{answerNum, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_PRM_SURVEY_USER /* 프로모션 설문 참여자 */
	USING DUAL ON (
		PRM_CD = #{prmCd, jdbcType=VARCHAR}	/* 프로모션 코드, VARCHAR2(10) */
		AND USER_ID = #{userId, jdbcType=VARCHAR}	/* 회원 ID, VARCHAR2(20) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 ANSWER_NUM = #{answerNum, jdbcType=NUMERIC}	/* 답변 번호, NUMBER(0,22) */
	WHEN NOT MATCHED THEN
		INSERT (
			 PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
			,USER_ID	/* 회원 ID, VARCHAR2(20) */
			,ANSWER_NUM	/* 답변 번호, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{prmCd, jdbcType=VARCHAR}
			,#{userId, jdbcType=VARCHAR}
			,#{answerNum, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
