mapper.insert:
INSERT INTO EZC_PRM_TAB(	/* 프로모션 TAB */
	 TAB_SEQ	/* TAB 일련번호, NUMBER(0,22) */
	,PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
	,TAB_NM	/* TAB 명, VARCHAR2(100) */
	,GOODS_DISP_ARR	/* 상품 노출 배열, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{tabSeq, jdbcType=DECIMAL}
	,#{prmCd, jdbcType=VARCHAR}
	,#{tabNm, jdbcType=VARCHAR}
	,#{goodsDispArr, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_PRM_TAB /* 프로모션 TAB */
	USING DUAL ON (
		TAB_SEQ = #{tabSeq, jdbcType=DECIMAL}	/* TAB 일련번호, NUMBER(0,22) */
		AND PRM_CD = #{prmCd, jdbcType=VARCHAR}	/* 프로모션 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 TAB_NM = #{tabNm, jdbcType=VARCHAR}	/* TAB 명, VARCHAR2(100) */
			,GOODS_DISP_ARR = #{goodsDispArr, jdbcType=NUMERIC}	/* 상품 노출 배열, NUMBER(0,22) */
	WHEN NOT MATCHED THEN
		INSERT (
			 TAB_SEQ	/* TAB 일련번호, NUMBER(0,22) */
			,PRM_CD	/* 프로모션 코드, VARCHAR2(10) */
			,TAB_NM	/* TAB 명, VARCHAR2(100) */
			,GOODS_DISP_ARR	/* 상품 노출 배열, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{tabSeq, jdbcType=DECIMAL}
			,#{prmCd, jdbcType=VARCHAR}
			,#{tabNm, jdbcType=VARCHAR}
			,#{goodsDispArr, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
