mapper.insert:
INSERT INTO EZC_QNA(	/* 시설 문의사항 */
	 QNA_SEQ	/* 문의사항 일련번호, NUMBER(0,22) */
	,QNA_GRP	/* 문의사항 그룹, NUMBER(0,22) */
	,QNA_GRP_ORD	/* 문의사항 그룹 순서, NUMBER(0,22) */
	,DEPTH	/* 깊이, NUMBER(0,22) */
	,TITLE	/* 제목, VARCHAR2(100) */
	,CONTENT	/* 내용, VARCHAR2(4000) */
	,PASS_YN	/* 비밀 여부, CHAR(1) */
	,QNA_STATUS	/* 문의사항 상태, VARCHAR2(8) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
	,FACL_CD	/* 시설 코드, NUMBER(0,22) */
) VALUES (
	 #{qnaSeq, jdbcType=DECIMAL}
	,#{qnaGrp, jdbcType=DECIMAL}
	,#{qnaGrpOrd, jdbcType=NUMERIC}
	,#{depth, jdbcType=NUMERIC}
	,#{title, jdbcType=VARCHAR}
	,#{content, jdbcType=VARCHAR}
	,#{passYn, jdbcType=CHAR}
	,#{qnaStatus, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
	,#{faclCd, jdbcType=DECIMAL}
)

mapper.update:
MERGE INTO EZC_QNA /* 시설 문의사항 */
	USING DUAL ON (
		QNA_SEQ = #{qnaSeq, jdbcType=DECIMAL}	/* 문의사항 일련번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 QNA_GRP = #{qnaGrp, jdbcType=DECIMAL}	/* 문의사항 그룹, NUMBER(0,22) */
			,QNA_GRP_ORD = #{qnaGrpOrd, jdbcType=NUMERIC}	/* 문의사항 그룹 순서, NUMBER(0,22) */
			,DEPTH = #{depth, jdbcType=NUMERIC}	/* 깊이, NUMBER(0,22) */
			,TITLE = #{title, jdbcType=VARCHAR}	/* 제목, VARCHAR2(100) */
			,CONTENT = #{content, jdbcType=VARCHAR}	/* 내용, VARCHAR2(4000) */
			,PASS_YN = #{passYn, jdbcType=CHAR}	/* 비밀 여부, CHAR(1) */
			,QNA_STATUS = #{qnaStatus, jdbcType=VARCHAR}	/* 문의사항 상태, VARCHAR2(8) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
			,FACL_CD = #{faclCd, jdbcType=DECIMAL}	/* 시설 코드, NUMBER(0,22) */
	WHEN NOT MATCHED THEN
		INSERT (
			 QNA_SEQ	/* 문의사항 일련번호, NUMBER(0,22) */
			,QNA_GRP	/* 문의사항 그룹, NUMBER(0,22) */
			,QNA_GRP_ORD	/* 문의사항 그룹 순서, NUMBER(0,22) */
			,DEPTH	/* 깊이, NUMBER(0,22) */
			,TITLE	/* 제목, VARCHAR2(100) */
			,CONTENT	/* 내용, VARCHAR2(4000) */
			,PASS_YN	/* 비밀 여부, CHAR(1) */
			,QNA_STATUS	/* 문의사항 상태, VARCHAR2(8) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
			,FACL_CD	/* 시설 코드, NUMBER(0,22) */
		) VALUES (
			 #{qnaSeq, jdbcType=DECIMAL}
			,#{qnaGrp, jdbcType=DECIMAL}
			,#{qnaGrpOrd, jdbcType=NUMERIC}
			,#{depth, jdbcType=NUMERIC}
			,#{title, jdbcType=VARCHAR}
			,#{content, jdbcType=VARCHAR}
			,#{passYn, jdbcType=CHAR}
			,#{qnaStatus, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
			,#{faclCd, jdbcType=DECIMAL}
		)
