mapper.update:
MERGE INTO EZC_RESERV_BASE /* 예약 기본 정보 */
	USING DUAL ON (
		RESERV_NUM = #{reservNum, jdbcType=DECIMAL}	/* 예약 번호, NUMBER(0,22) */
		AND FACL_CD = #{faclCd, jdbcType=DECIMAL}	/* 시설 코드, NUMBER(0,22) */
		AND PARTNER_CD = #{partnerCd, jdbcType=VARCHAR}	/* 제휴사 코드, VARCHAR2(20) */
		AND CHANNEL_CD = #{channelCd, jdbcType=VARCHAR}	/* 채널 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 OFF_YN = #{offYn, jdbcType=CHAR}	/* 오프라인 여부, CHAR(1) */
			,EZWEL_ORDER_NUM = #{ezwelOrderNum, jdbcType=DECIMAL}	/* 이지웰 주문 번호, NUMBER(0,22) */
			,PARTNER_ORDER_NUM = #{partnerOrderNum, jdbcType=VARCHAR}	/* 제휴사 주문 번호, VARCHAR2(100) */
			,FACL_RESERV_NUM = #{faclReservNum, jdbcType=VARCHAR}	/* 시설 예약 번호, VARCHAR2(100) */
			,DEVICE_DIV = #{deviceDiv, jdbcType=VARCHAR}	/* 디바이스 구분, VARCHAR2(8) */
			,USER_KEY = #{userKey, jdbcType=DECIMAL}	/* USER_KEY, NUMBER(0,22) */
