			,CHECK_OUT_DD = #{checkOutDd, jdbcType=VARCHAR}	/* 체크아웃 일자, VARCHAR2(8) */
			,TOT_ADT_CNT = #{totAdtCnt, jdbcType=NUMERIC}	/* 총 성인 수, NUMBER(0,22) */
			,TOT_CHILD_CNT = #{totChildCnt, jdbcType=NUMERIC}	/* 총 소아 수, NUMBER(0,22) */
			,GUEST_NM = #{guestNm, jdbcType=VARCHAR}	/* 투숙자 명, VARCHAR2(20) */
			,GUEST_MOBILE = #{guestMobile, jdbcType=VARCHAR}	/* 투숙자 휴대전화, VARCHAR2(20) */
			,GUEST_EMAIL = #{guestEmail, jdbcType=VARCHAR}	/* 투숙자 이메일, VARCHAR2(100) */
			,RESERV_FAX_SEND_DT = #{reservFaxSendDt, jdbcType=VARCHAR}	/* 예약 팩스 발송 일시, VARCHAR2(14) */
			,RESERV_FAX_RECV_DT = #{reservFaxRecvDt, jdbcType=VARCHAR}	/* 예약 팩스 수신 일시, VARCHAR2(14) */
			,RESERV_DT = #{reservDt, jdbcType=VARCHAR}	/* 예약 일시, VARCHAR2(14) */
			,RESERV_CANCEL_DT = #{reservCancelDt, jdbcType=VARCHAR}	/* 예약 취소 일시, VARCHAR2(14) */
			,RESERV_STATUS = #{reservStatus, jdbcType=VARCHAR}	/* 예약 상태, VARCHAR2(8) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
			,USER_ID = #{userId, jdbcType=VARCHAR}	/* 회원 ID, VARCHAR2(20) */
	WHEN NOT MATCHED THEN
		INSERT (
			 RESERV_NUM	/* 예약 번호, NUMBER(0,22) */
			,FACL_CD	/* 시설 코드, NUMBER(0,22) */
			,OFF_YN	/* 오프라인 여부, CHAR(1) */
			,EZWEL_ORDER_NUM	/* 이지웰 주문 번호, NUMBER(0,22) */
			,PARTNER_ORDER_NUM	/* 제휴사 주문 번호, VARCHAR2(100) */
			,FACL_RESERV_NUM	/* 시설 예약 번호, VARCHAR2(100) */
			,DEVICE_DIV	/* 디바이스 구분, VARCHAR2(8) */
			,PARTNER_CD	/* 제휴사 코드, VARCHAR2(20) */
			,CHANNEL_CD	/* 채널 코드, VARCHAR2(10) */
			,USER_KEY	/* USER_KEY, NUMBER(0,22) */
			,FACL_NM	/* 시설 명, VARCHAR2(100) */
			,TOT_SALE_AMT	/* 총 판매 금액, NUMBER(0,22) */
