mapper.insert:
INSERT INTO EZC_RESERV_CANCEL(	/* 예약 취소 정보 */
	 RESERV_CANCEL_SEQ	/* 예약 취소 일련번호, NUMBER(0,22) */
	,RESERV_NUM	/* 예약 번호, NUMBER(0,22) */
	,RESERV_ROOM_NUM	/* 예약 객실 번호, NUMBER(0,22) */
	,ALL_CANCEL_YN	/* 전체 취소 여부, CHAR(1) */
	,CANCEL_REQ_DT	/* 취소 요청 일시, VARCHAR2(14) */
	,CANCEL_FEE_AMT	/* 취소 수수료 금액, NUMBER(0,22) */
	,FEE_PAY_YN	/* 수수료 결제 여부, CHAR(1) */
	,CANCEL_STATUS	/* 취소 상태, VARCHAR2(8) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{reservCancelSeq, jdbcType=DECIMAL}
	,#{reservNum, jdbcType=DECIMAL}
	,#{reservRoomNum, jdbcType=DECIMAL}
	,#{allCancelYn, jdbcType=CHAR}
	,#{cancelReqDt, jdbcType=VARCHAR}
	,#{cancelFeeAmt, jdbcType=NUMERIC}
	,#{feePayYn, jdbcType=CHAR}
	,#{cancelStatus, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_RESERV_CANCEL /* 예약 취소 정보 */
	USING DUAL ON (
		RESERV_CANCEL_SEQ = #{reservCancelSeq, jdbcType=DECIMAL}	/* 예약 취소 일련번호, NUMBER(0,22) */
		AND RESERV_NUM = #{reservNum, jdbcType=DECIMAL}	/* 예약 번호, NUMBER(0,22) */
		AND RESERV_ROOM_NUM = #{reservRoomNum, jdbcType=DECIMAL}	/* 예약 객실 번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 ALL_CANCEL_YN = #{allCancelYn, jdbcType=CHAR}	/* 전체 취소 여부, CHAR(1) */
			,CANCEL_REQ_DT = #{cancelReqDt, jdbcType=VARCHAR}	/* 취소 요청 일시, VARCHAR2(14) */
			,CANCEL_FEE_AMT = #{cancelFeeAmt, jdbcType=NUMERIC}	/* 취소 수수료 금액, NUMBER(0,22) */
			,FEE_PAY_YN = #{feePayYn, jdbcType=CHAR}	/* 수수료 결제 여부, CHAR(1) */
			,CANCEL_STATUS = #{cancelStatus, jdbcType=VARCHAR}	/* 취소 상태, VARCHAR2(8) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 RESERV_CANCEL_SEQ	/* 예약 취소 일련번호, NUMBER(0,22) */
			,RESERV_NUM	/* 예약 번호, NUMBER(0,22) */
			,RESERV_ROOM_NUM	/* 예약 객실 번호, NUMBER(0,22) */
			,ALL_CANCEL_YN	/* 전체 취소 여부, CHAR(1) */
			,CANCEL_REQ_DT	/* 취소 요청 일시, VARCHAR2(14) */
			,CANCEL_FEE_AMT	/* 취소 수수료 금액, NUMBER(0,22) */
			,FEE_PAY_YN	/* 수수료 결제 여부, CHAR(1) */
			,CANCEL_STATUS	/* 취소 상태, VARCHAR2(8) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{reservCancelSeq, jdbcType=DECIMAL}
			,#{reservNum, jdbcType=DECIMAL}
			,#{reservRoomNum, jdbcType=DECIMAL}
			,#{allCancelYn, jdbcType=CHAR}
			,#{cancelReqDt, jdbcType=VARCHAR}
			,#{cancelFeeAmt, jdbcType=NUMERIC}
			,#{feePayYn, jdbcType=CHAR}
			,#{cancelStatus, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
