mapper.insert:
INSERT INTO EZC_RESERV_MNG_HIS(	/* 예약 관리 이력 */
	 MNGHIS_SEQ	/* 관리이력 일련번호, NUMBER(0,22) */
	,RESERV_NUM	/* 예약 번호, NUMBER(0,22) */
	,RESERV_MNG_HIS_DIV	/* 예약 관리 이력 구분, VARCHAR2(8) */
	,CONTENT	/* 내용, VARCHAR2(4000) */
	,SEND_DT	/* 발송 일시, VARCHAR2(14) */
	,RECV_DT	/* 수신 일시, VARCHAR2(14) */
	,AUTO_SEND_YN	/* 자동 발송 여부, CHAR(1) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{mnghisSeq, jdbcType=DECIMAL}
	,#{reservNum, jdbcType=DECIMAL}
	,#{reservMngHisDiv, jdbcType=VARCHAR}
	,#{content, jdbcType=VARCHAR}
	,#{sendDt, jdbcType=VARCHAR}
	,#{recvDt, jdbcType=VARCHAR}
	,#{autoSendYn, jdbcType=CHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_RESERV_MNG_HIS /* 예약 관리 이력 */
	USING DUAL ON (
		MNGHIS_SEQ = #{mnghisSeq, jdbcType=DECIMAL}	/* 관리이력 일련번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 RESERV_NUM = #{reservNum, jdbcType=DECIMAL}	/* 예약 번호, NUMBER(0,22) */
			,RESERV_MNG_HIS_DIV = #{reservMngHisDiv, jdbcType=VARCHAR}	/* 예약 관리 이력 구분, VARCHAR2(8) */
			,CONTENT = #{content, jdbcType=VARCHAR}	/* 내용, VARCHAR2(4000) */
			,SEND_DT = #{sendDt, jdbcType=VARCHAR}	/* 발송 일시, VARCHAR2(14) */
			,RECV_DT = #{recvDt, jdbcType=VARCHAR}	/* 수신 일시, VARCHAR2(14) */
			,AUTO_SEND_YN = #{autoSendYn, jdbcType=CHAR}	/* 자동 발송 여부, CHAR(1) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 MNGHIS_SEQ	/* 관리이력 일련번호, NUMBER(0,22) */
			,RESERV_NUM	/* 예약 번호, NUMBER(0,22) */
			,RESERV_MNG_HIS_DIV	/* 예약 관리 이력 구분, VARCHAR2(8) */
			,CONTENT	/* 내용, VARCHAR2(4000) */
			,SEND_DT	/* 발송 일시, VARCHAR2(14) */
			,RECV_DT	/* 수신 일시, VARCHAR2(14) */
			,AUTO_SEND_YN	/* 자동 발송 여부, CHAR(1) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{mnghisSeq, jdbcType=DECIMAL}
			,#{reservNum, jdbcType=DECIMAL}
			,#{reservMngHisDiv, jdbcType=VARCHAR}
			,#{content, jdbcType=VARCHAR}
			,#{sendDt, jdbcType=VARCHAR}
			,#{recvDt, jdbcType=VARCHAR}
			,#{autoSendYn, jdbcType=CHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
