mapper.insert:
INSERT INTO EZC_RESERV_PAY(	/* 예약 결제 정보 */
	 RESERV_NUM	/* 예약 번호, NUMBER(0,22) */
	,CARD_PAY_YN	/* 카드 결제 여부, CHAR(1) */
	,CARD_PAY_AMT	/* 카드 결제 금액, NUMBER(0,22) */
	,CASH_PAY_YN	/* 현금 결제 여부, CHAR(1) */
	,CASH_PAY_AMT	/* 현금 결제 금액, NUMBER(0,22) */
	,CPN_USE_YN	/* 쿠폰 사용 여부, CHAR(1) */
	,DC_CPN_AMT	/* 할인 쿠폰 금액, NUMBER(0,22) */
	,POINT_USE_YN	/* 포인트 사용 여부, CHAR(1) */
	,POINT_AMT	/* 포인트 금액, NUMBER(0,22) */
	,NON_AMT	/* 미납 금액, NUMBER(0,22) */
	,PAY_STATUS	/* 결제 상태, VARCHAR2(8) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{reservNum, jdbcType=DECIMAL}
	,#{cardPayYn, jdbcType=CHAR}
	,#{cardPayAmt, jdbcType=NUMERIC}
	,#{cashPayYn, jdbcType=CHAR}
	,#{cashPayAmt, jdbcType=NUMERIC}
	,#{cpnUseYn, jdbcType=CHAR}
	,#{dcCpnAmt, jdbcType=NUMERIC}
	,#{pointUseYn, jdbcType=CHAR}
	,#{pointAmt, jdbcType=NUMERIC}
	,#{nonAmt, jdbcType=NUMERIC}
	,#{payStatus, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_RESERV_PAY /* 예약 결제 정보 */
	USING DUAL ON (
		RESERV_NUM = #{reservNum, jdbcType=DECIMAL}	/* 예약 번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 CARD_PAY_YN = #{cardPayYn, jdbcType=CHAR}	/* 카드 결제 여부, CHAR(1) */
			,CARD_PAY_AMT = #{cardPayAmt, jdbcType=NUMERIC}	/* 카드 결제 금액, NUMBER(0,22) */
			,CASH_PAY_YN = #{cashPayYn, jdbcType=CHAR}	/* 현금 결제 여부, CHAR(1) */
			,CASH_PAY_AMT = #{cashPayAmt, jdbcType=NUMERIC}	/* 현금 결제 금액, NUMBER(0,22) */
			,CPN_USE_YN = #{cpnUseYn, jdbcType=CHAR}	/* 쿠폰 사용 여부, CHAR(1) */
			,DC_CPN_AMT = #{dcCpnAmt, jdbcType=NUMERIC}	/* 할인 쿠폰 금액, NUMBER(0,22) */
			,POINT_USE_YN = #{pointUseYn, jdbcType=CHAR}	/* 포인트 사용 여부, CHAR(1) */
			,POINT_AMT = #{pointAmt, jdbcType=NUMERIC}	/* 포인트 금액, NUMBER(0,22) */
			,NON_AMT = #{nonAmt, jdbcType=NUMERIC}	/* 미납 금액, NUMBER(0,22) */
			,PAY_STATUS = #{payStatus, jdbcType=VARCHAR}	/* 결제 상태, VARCHAR2(8) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 RESERV_NUM	/* 예약 번호, NUMBER(0,22) */
			,CARD_PAY_YN	/* 카드 결제 여부, CHAR(1) */
			,CARD_PAY_AMT	/* 카드 결제 금액, NUMBER(0,22) */
			,CASH_PAY_YN	/* 현금 결제 여부, CHAR(1) */
			,CASH_PAY_AMT	/* 현금 결제 금액, NUMBER(0,22) */
			,CPN_USE_YN	/* 쿠폰 사용 여부, CHAR(1) */
			,DC_CPN_AMT	/* 할인 쿠폰 금액, NUMBER(0,22) */
			,POINT_USE_YN	/* 포인트 사용 여부, CHAR(1) */
			,POINT_AMT	/* 포인트 금액, NUMBER(0,22) */
			,NON_AMT	/* 미납 금액, NUMBER(0,22) */
			,PAY_STATUS	/* 결제 상태, VARCHAR2(8) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{reservNum, jdbcType=DECIMAL}
			,#{cardPayYn, jdbcType=CHAR}
			,#{cardPayAmt, jdbcType=NUMERIC}
			,#{cashPayYn, jdbcType=CHAR}
			,#{cashPayAmt, jdbcType=NUMERIC}
			,#{cpnUseYn, jdbcType=CHAR}
			,#{dcCpnAmt, jdbcType=NUMERIC}
			,#{pointUseYn, jdbcType=CHAR}
			,#{pointAmt, jdbcType=NUMERIC}
			,#{nonAmt, jdbcType=NUMERIC}
			,#{payStatus, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
