mapper.update:
MERGE INTO EZC_RESERV_ROOM /* 예약 객실 정보 */
	USING DUAL ON (
		RESERV_ROOM_NUM = #{reservRoomNum, jdbcType=DECIMAL}	/* 예약 객실 번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 RESERV_NUM = #{reservNum, jdbcType=DECIMAL}	/* 예약 번호, NUMBER(0,22) */
			,PARTNER_ROOM_ORDER_NUM = #{partnerRoomOrderNum, jdbcType=VARCHAR}	/* 제휴사 객실 주문 번호, VARCHAR2(100) */
