mapper.insert:
INSERT INTO EZC_RESERV_ROOM_OPT(	/* 예약 객실 옵션 */
	 RESERV_OPT_NUM	/* 예약 옵션 번호, NUMBER(0,22) */
	,RESERV_ROOM_NUM	/* 예약 객실 번호, NUMBER(0,22) */
	,PARTNER_OPT_ORDER_NUM	/* 제휴사 옵션 주문 번호, VARCHAR2(100) */
	,PARTNER_OPT_CD	/* 제휴사 옵션 코드, VARCHAR2(100) */
	,OPT_NM	/* 옵션 명, VARCHAR2(100) */
	,OPT_AMT	/* 옵션 금액, NUMBER(0,22) */
	,OPT_QTY	/* 옵션 수량, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
	,DIST_OPT_CD	/* 직영/숙박 옵션 코드, NUMBER(0,22) */
) VALUES (
	 #{reservOptNum, jdbcType=DECIMAL}
	,#{reservRoomNum, jdbcType=DECIMAL}
	,#{partnerOptOrderNum, jdbcType=VARCHAR}
	,#{partnerOptCd, jdbcType=VARCHAR}
	,#{optNm, jdbcType=VARCHAR}
	,#{optAmt, jdbcType=NUMERIC}
	,#{optQty, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
	,#{distOptCd, jdbcType=DECIMAL}
)

mapper.update:
MERGE INTO EZC_RESERV_ROOM_OPT /* 예약 객실 옵션 */
	USING DUAL ON (
		RESERV_OPT_NUM = #{reservOptNum, jdbcType=DECIMAL}	/* 예약 옵션 번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 RESERV_ROOM_NUM = #{reservRoomNum, jdbcType=DECIMAL}	/* 예약 객실 번호, NUMBER(0,22) */
			,PARTNER_OPT_ORDER_NUM = #{partnerOptOrderNum, jdbcType=VARCHAR}	/* 제휴사 옵션 주문 번호, VARCHAR2(100) */
			,PARTNER_OPT_CD = #{partnerOptCd, jdbcType=VARCHAR}	/* 제휴사 옵션 코드, VARCHAR2(100) */
			,OPT_NM = #{optNm, jdbcType=VARCHAR}	/* 옵션 명, VARCHAR2(100) */
			,OPT_AMT = #{optAmt, jdbcType=NUMERIC}	/* 옵션 금액, NUMBER(0,22) */
			,OPT_QTY = #{optQty, jdbcType=NUMERIC}	/* 옵션 수량, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
			,DIST_OPT_CD = #{distOptCd, jdbcType=DECIMAL}	/* 직영/숙박 옵션 코드, NUMBER(0,22) */
	WHEN NOT MATCHED THEN
		INSERT (
			 RESERV_OPT_NUM	/* 예약 옵션 번호, NUMBER(0,22) */
			,RESERV_ROOM_NUM	/* 예약 객실 번호, NUMBER(0,22) */
			,PARTNER_OPT_ORDER_NUM	/* 제휴사 옵션 주문 번호, VARCHAR2(100) */
			,PARTNER_OPT_CD	/* 제휴사 옵션 코드, VARCHAR2(100) */
			,OPT_NM	/* 옵션 명, VARCHAR2(100) */
			,OPT_AMT	/* 옵션 금액, NUMBER(0,22) */
			,OPT_QTY	/* 옵션 수량, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
			,DIST_OPT_CD	/* 직영/숙박 옵션 코드, NUMBER(0,22) */
		) VALUES (
			 #{reservOptNum, jdbcType=DECIMAL}
			,#{reservRoomNum, jdbcType=DECIMAL}
			,#{partnerOptOrderNum, jdbcType=VARCHAR}
			,#{partnerOptCd, jdbcType=VARCHAR}
			,#{optNm, jdbcType=VARCHAR}
			,#{optAmt, jdbcType=NUMERIC}
			,#{optQty, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
			,#{distOptCd, jdbcType=DECIMAL}
		)
