mapper.insert:
INSERT INTO EZC_SPOT(	/* 스팟 */
	 SPOT_CD	/* 스팟 코드, NUMBER(0,22) */
	,CITY_CD	/* 도시 코드, VARCHAR2(10) */
	,AREA_CD	/* 지역 코드, VARCHAR2(10) */
	,SPOT_NM	/* 스팟 명, VARCHAR2(100) */
	,APPLY_START_DD	/* 적용 시작 일자, VARCHAR2(8) */
	,APPLY_END_DD	/* 적용 종료 일자, VARCHAR2(8) */
	,COORD_Y	/* 위도, VARCHAR2(20) */
	,COORD_X	/* 경도, VARCHAR2(20) */
	,URL	/* URL, VARCHAR2(200) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{spotCd, jdbcType=DECIMAL}
	,#{cityCd, jdbcType=VARCHAR}
	,#{areaCd, jdbcType=VARCHAR}
	,#{spotNm, jdbcType=VARCHAR}
	,#{applyStartDd, jdbcType=VARCHAR}
	,#{applyEndDd, jdbcType=VARCHAR}
	,#{coordY, jdbcType=VARCHAR}
	,#{coordX, jdbcType=VARCHAR}
	,#{url, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_SPOT /* 스팟 */
	USING DUAL ON (
		SPOT_CD = #{spotCd, jdbcType=DECIMAL}	/* 스팟 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 CITY_CD = #{cityCd, jdbcType=VARCHAR}	/* 도시 코드, VARCHAR2(10) */
			,AREA_CD = #{areaCd, jdbcType=VARCHAR}	/* 지역 코드, VARCHAR2(10) */
			,SPOT_NM = #{spotNm, jdbcType=VARCHAR}	/* 스팟 명, VARCHAR2(100) */
			,APPLY_START_DD = #{applyStartDd, jdbcType=VARCHAR}	/* 적용 시작 일자, VARCHAR2(8) */
			,APPLY_END_DD = #{applyEndDd, jdbcType=VARCHAR}	/* 적용 종료 일자, VARCHAR2(8) */
			,COORD_Y = #{coordY, jdbcType=VARCHAR}	/* 위도, VARCHAR2(20) */
			,COORD_X = #{coordX, jdbcType=VARCHAR}	/* 경도, VARCHAR2(20) */
			,URL = #{url, jdbcType=VARCHAR}	/* URL, VARCHAR2(200) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 SPOT_CD	/* 스팟 코드, NUMBER(0,22) */
			,CITY_CD	/* 도시 코드, VARCHAR2(10) */
			,AREA_CD	/* 지역 코드, VARCHAR2(10) */
			,SPOT_NM	/* 스팟 명, VARCHAR2(100) */
			,APPLY_START_DD	/* 적용 시작 일자, VARCHAR2(8) */
			,APPLY_END_DD	/* 적용 종료 일자, VARCHAR2(8) */
			,COORD_Y	/* 위도, VARCHAR2(20) */
			,COORD_X	/* 경도, VARCHAR2(20) */
			,URL	/* URL, VARCHAR2(200) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{spotCd, jdbcType=DECIMAL}
			,#{cityCd, jdbcType=VARCHAR}
			,#{areaCd, jdbcType=VARCHAR}
			,#{spotNm, jdbcType=VARCHAR}
			,#{applyStartDd, jdbcType=VARCHAR}
			,#{applyEndDd, jdbcType=VARCHAR}
			,#{coordY, jdbcType=VARCHAR}
			,#{coordX, jdbcType=VARCHAR}
			,#{url, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
