mapper.insert:
INSERT INTO EZC_SPOT_MAPPING_FACL(	/* 스팟 맵핑 시설 */
	 SPOT_CD	/* 스팟 코드, NUMBER(0,22) */
	,GRP_FACL_CD	/* 그룹 시설 코드, NUMBER(0,22) */
	,DISP_ORDER	/* 정렬 순서, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{spotCd, jdbcType=DECIMAL}
	,#{grpFaclCd, jdbcType=DECIMAL}
	,#{dispOrder, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_SPOT_MAPPING_FACL /* 스팟 맵핑 시설 */
	USING DUAL ON (
		SPOT_CD = #{spotCd, jdbcType=DECIMAL}	/* 스팟 코드, NUMBER(0,22) */
		AND GRP_FACL_CD = #{grpFaclCd, jdbcType=DECIMAL}	/* 그룹 시설 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 DISP_ORDER = #{dispOrder, jdbcType=NUMERIC}	/* 정렬 순서, NUMBER(0,22) */
	WHEN NOT MATCHED THEN
		INSERT (
			 SPOT_CD	/* 스팟 코드, NUMBER(0,22) */
			,GRP_FACL_CD	/* 그룹 시설 코드, NUMBER(0,22) */
			,DISP_ORDER	/* 정렬 순서, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{spotCd, jdbcType=DECIMAL}
			,#{grpFaclCd, jdbcType=DECIMAL}
			,#{dispOrder, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
